(function() {
  var FirebaseResponse;

  FirebaseResponse = (function() {
    function FirebaseResponse(res) {
      this.body = res.body;
      this.statusCode = res.statusCode;
    }

    FirebaseResponse.prototype.success = function() {
      return this.statusCode >= 200 && this.statusCode <= 299;
    };

    FirebaseResponse.prototype.clientError = function() {
      if (this.statusCode >= 400 && this.statusCode <= 499) {
        return new Error("HTTP client error " + this.statusCode);
      }
    };

    FirebaseResponse.prototype.serverError = function() {
      if (this.statusCode >= 500 && this.statusCode <= 599) {
        return new Error("HTTP server error " + this.statusCode);
      }
    };

    return FirebaseResponse;

  })();

  module.exports = FirebaseResponse;

}).call(this);
